import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../../services/api";
import "./Auth.css";

const Login = () => {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await loginUser(formData);
      // IMPORTANT : Axios => data.token
      const token = response?.data?.token;

      if (!token) {
        setError("Réponse inattendue du serveur (pas de token).");
        return;
      }

      // IMPORTANT : la clé doit être "token" pour l'intercepteur
     localStorage.setItem("token", token);
    


      navigate("/dashboard");
    } catch (err) {
      // 404 => route inexistante côté backend, 401 => mauvais identifiants
      const msg =
        err?.response?.status === 404
          ? "Route /api/auth/login introuvable côté serveur."
          : err?.response?.data?.message || "Identifiants incorrects.";
      setError(msg);
      console.error(err);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Connexion à GreenCampus</h2>

        {error && <p className="error">{error}</p>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="form-group">
            <label>Mot de passe</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="auth-btn">Se connecter</button>
        </form>

        <p className="auth-footer">
          Pas encore inscrit ?{" "}
          <span onClick={() => navigate("/register")}>Créer un compte</span>
        </p>
      </div>
    </div>
  );
};

export default Login;


