import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import "./Dashboard.css";


const Dashboard = () => {
  const [stats, setStats] = useState({
    totalActions: 0,
    completedActions: 0,
    upcomingActions: 0,
    participationRate: 0
  });

  const [recentActions, setRecentActions] = useState([]);

 useEffect(() => {
  const token = localStorage.getItem("token"); // supposons que tu stockes le JWT après login

  fetch("http://localhost:5000/api/action", {
    headers: {
      "Authorization": `Bearer ${token}`
    }
  })
    .then(res => {
      if (!res.ok) throw new Error("Erreur API: " + res.status);
      return res.json();
    })
    .then(data => {
      setRecentActions(
        data.map(a => ({
          id: a.id,
          title: a.titre,
          date: a.date_debut,
          status: mapStatus(a.statut)
        }))
      );

      setStats({
        totalActions: data.length,
        completedActions: data.filter(a => a.statut === "terminé").length,
        upcomingActions: data.filter(a => a.statut === "planifié").length,
        participationRate: Math.round(
          (data.filter(a => a.statut === "en_cours").length / (data.length || 1)) * 100
        )
      });
    })
    .catch(err => console.error("❌ Erreur fetch actions:", err));
}, []);


const mapStatus = (status) => {
  switch (status) {
    case "planifié":
      return "planned";
    case "en_cours":
      return "in-progress";
    case "terminé":
      return "completed";
    default:
      return "planned";
  }
};


  return (
    <div className="dashboard">
      <h1>Tableau de Bord RSE</h1>
      
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Actions totales</h3>
          <p className="stat-number">{stats.totalActions}</p>
        </div>
        <div className="stat-card">
          <h3>Actions complétées</h3>
          <p className="stat-number">{stats.completedActions}</p>
        </div>
        <div className="stat-card">
          <h3>Actions à venir</h3>
          <p className="stat-number">{stats.upcomingActions}</p>
        </div>
        <div className="stat-card">
          <h3>Taux de participation</h3>
          <p className="stat-number">{stats.participationRate}%</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <section className="recent-actions">
          <h2>Actions récentes</h2>
          <ul>
            {recentActions.map(action => (
              <li key={action.id} className="action-item">
                <span className={`status-indicator ${action.status}`}></span>
                <div className="action-info">
                  <h4>{action.title}</h4>
                  <p>Date: {action.date}</p>
                </div>
                <Link to={`/actiondetail/${action.id}`} className="view-details">Voir détails</Link>
              </li>
            ))}
          </ul>
        </section>

        <section className="quick-actions">
          <h2>Actions rapides</h2>
          <div className="action-buttons">
            <Link to="/create-action" className="btn btn-primary">Créer une action</Link>
            <Link to="/planning" className="btn btn-secondary">Voir le planning</Link>
            <Link to="/newsletter" className="btn btn-tertiary">Gérer newsletter</Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Dashboard;
