import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./Newsletter.css";

const Newsletter = () => {
  const [newsletters, setNewsletters] = useState([]);
  const [newNewsletter, setNewNewsletter] = useState({ titre: '', contenu: '', statut: 'brouillon' });

  // Charger toutes les newsletters depuis l’API
  useEffect(() => {
    axios.get("http://localhost:5000/api/newsletters")
      .then(res => setNewsletters(res.data))
      .catch(err => console.error(err));
  }, []);

  // Ajouter une nouvelle newsletter
  const handleAddNewsletter = (e) => {
    e.preventDefault();
    axios.post("http://localhost:5000/api/newsletters", newNewsletter)
      .then(res => {
        setNewsletters([res.data, ...newsletters]); // ajout immédiat
        setNewNewsletter({ titre: '', contenu: '', statut: 'brouillon' });
      })
      .catch(err => console.error(err));
  };

  // Supprimer une newsletter
  const handleDeleteNewsletter = (id) => {
    axios.delete(`http://localhost:5000/api/newsletters/${id}`)
      .then(() => setNewsletters(newsletters.filter(n => n.id !== id)))
      .catch(err => console.error(err));
  };

  return (
    <div className="newsletter">
      <h1>Gestion des Newsletters</h1>

      {/* Formulaire d’ajout */}
      <form onSubmit={handleAddNewsletter} className="add-subscriber-form">
        <input
          type="text"
          placeholder="Titre"
          value={newNewsletter.titre}
          onChange={(e) => setNewNewsletter({...newNewsletter, titre: e.target.value})}
          required
        />
        <textarea
          placeholder="Contenu..."
          value={newNewsletter.contenu}
          onChange={(e) => setNewNewsletter({...newNewsletter, contenu: e.target.value})}
          rows="4"
          required
        />
        <select
          value={newNewsletter.statut}
          onChange={(e) => setNewNewsletter({...newNewsletter, statut: e.target.value})}
        >
          <option value="brouillon">Brouillon</option>
          <option value="publié">Publié</option>
        </select>
        <button type="submit" className="btn btn-primary">Ajouter Newsletter</button>
      </form>

      {/* Liste des newsletters */}
      <div className="subscribers-list">
        {newsletters.map(n => (
          <div key={n.id} className="subscriber-item">
            <div className="subscriber-info">
              <h4>{n.titre}</h4>
              <p>{n.contenu}</p>
              <small>Publié le : {new Date(n.date_publication).toLocaleDateString()} | Statut : {n.statut}</small>
            </div>
            <button 
              onClick={() => handleDeleteNewsletter(n.id)}
              className="btn btn-danger"
            >
              Supprimer
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Newsletter;





















