import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "./ActionDetail.css";

const ActionDetail = () => {
  const { id } = useParams();
  const [action, setAction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAction = async () => {
      try {
        const token = localStorage.getItem("token"); // üîë r√©cup√®re le JWT stock√©

        if (!token) {
          setError("‚ö†Ô∏è Vous devez √™tre connect√©");
          setLoading(false);
          return;
        }

        const response = await fetch(`http://localhost:5000/api/action/${id}`, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // üîë Envoi du token
          },
        });

        if (!response.ok) throw new Error("Erreur lors du chargement");

        const data = await response.json();

        // Mapping backend -> frontend
        setAction({
          id: data.id,
          title: data.titre,
          description: data.description,
          startDate: data.date_debut,
          endDate: data.date_fin,
          status: data.statut,
          target: data.populations || "Non d√©fini",
          actors: data.createur || "Non d√©fini",
          resources: data.ressources || "Non d√©fini",
          kpis: data.kpis || [],
          materials: data.materials || [],
          etablissement: data.etablissement_nom
            ? `${data.etablissement_nom} (${data.etablissement_type}, ${data.etablissement_localisation})`
            : "Non d√©fini",
        });
      } catch (err) {
        console.error("‚ùå Erreur fetch action:", err);
        setError("Impossible de charger l'action");
      } finally {
        setLoading(false);
      }
    };

    fetchAction();
  }, [id]);

  if (loading) return <div className="loading">Chargement...</div>;
  if (error) return <div className="error">{error}</div>;
