import React from "react";
import { Link, useLocation } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();

  // Simule un utilisateur connect√©
  const user = {
    name: "Jean Dupont",
    avatar: "https://ui-avatars.com/api/?name=Jean+Dupont&background=2563eb&color=fff"
  };

  // Cacher la navbar pour login et register
  if (location.pathname === "/login" || location.pathname === "/register") {
    return null;
  }

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/dashboard" className="app-title">
          üåç RSE App
        </Link>
        <ul className="nav-links">
          <li><Link to="/dashboard">Dashboard</Link></li>
          <li><Link to="/planning">Planning</Link></li>
          <li><Link to="/newsletter">Newsletter</Link></li>
          <li><Link to="/create-action">Cr√©er Action</Link></li>
        </ul>
      </div>

      <div className="navbar-right">
        <img src={user.avatar} alt="avatar" className="user-avatar" />
        <span className="user-name">{user.name}</span>
      </div>
      
    </nav>
  );
};

export default Navbar;
