import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import "./Planning.css";

const Planning = () => {
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth());
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [actions, setActions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchActions();
  }, []);

  const fetchActions = async () => {
    try {
      setLoading(true);
      setError(null);

      // üîë R√©cup√©ration du token JWT stock√© au login
      const token = localStorage.getItem("token");

      if (!token) {
        setError("‚ö†Ô∏è Vous devez √™tre connect√© pour acc√©der au planning");
        setLoading(false);
        return;
      }

      const apiUrl = 'http://localhost:5000/api/action';
      console.log('Tentative de connexion √†:', apiUrl);

      const response = await fetch(apiUrl, {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`, // ‚úÖ Envoi du token
        },
      });

      if (!response.ok) {
        throw new Error(`Erreur HTTP: ${response.status}`);
      }

      const data = await response.json();
      setActions(data);
    } catch (err) {
      console.error('Erreur d√©taill√©e:', err);

      if (err.message.includes('Failed to fetch')) {
        setError('Impossible de se connecter au serveur backend. V√©rifiez que le serveur est en cours d\'ex√©cution sur le port 5000.');
      } else {
        setError(`Erreur lors du chargement: ${err.message}`);
      }
    } finally {
      setLoading(false);
    }
  };

  const months = [
    'Janvier', 'F√©vrier', 'Mars', 'Avril', 'Mai', 'Juin',
    'Juillet', 'Ao√ªt', 'Septembre', 'Octobre', 'Novembre', 'D√©cembre'
  ];

  const generateYears = () => {
    const currentYear = new Date().getFullYear();
    return [currentYear - 1, currentYear, currentYear + 1];
  };

  const filteredActions = actions.filter(action => {
    if (!action.date_debut) return false;

    const actionDate = new Date(action.date_debut);
    return actionDate.getMonth() === selectedMonth && 
           actionDate.getFullYear() === selectedYear;
  });

  if (loading) {
    return (
      <div className="planning">
        <h1>Planning des Actions RSE</h1>
        <div className="loading">Chargement des actions...</div>
      </div>
    );
  }

  return (
    <div className="planning">
      <h1>Planning des Actions RSE</h1>

      {error && (
        <div className="error-message">
          <p>{error}</p>
          <div className="troubleshooting">
            <h3>D√©pannage :</h3>
            <ul>
              <li>Le backend fonctionne sur le port 5000</li>
              <li>V√©rifiez que l'URL est correcte: http://localhost:5000/api/action</li>
              <li>Assurez-vous que le serveur backend est toujours en cours d'ex√©cution</li>
              <li>‚ö†Ô∏è V√©rifiez que vous √™tes bien connect√© et que le token JWT est valide</li>
            </ul>
          </div>
          <button onClick={fetchActions} className="retry-button">
            R√©essayer
          </button>
        </div>
      )}

      {!error && (
        <>
          <div className="planning-controls">
            <select 
              value={selectedMonth} 
              onChange={(e) => setSelectedMonth(parseInt(e.target.value))}
            >
              {months.map((month, index) => (
                <option key={index} value={index}>{month}</option>
              ))}
            </select>

            <select 
              value={selectedYear} 
              onChange={(e) => setSelectedYear(parseInt(e.target.value))}
            >
              {generateYears().map(year => (
                <option key={year} value={year}>{year}</option>
              ))}
            </select>
          </div>

          <div className="calendar">
            {filteredActions.length > 0 ? (
              filteredActions.map(action => (
                <div key={action.id} className="calendar-event">
                  <div className="event-date">
                    {new Date(action.date_debut).toLocaleDateString('fr-FR', {
                      day: 'numeric',
                      month: 'long'
                    })}
                  </div>
                  <div className="event-details">
                    <h3>{action.titre}</h3>
                    <span className={`event-status ${action.statut}`}>
                      {action.statut === 'planifi√©' ? 'Planifi√©e' : 
                      action.statut === 'en_cours' ? 'En cours' : 'Termin√©e'}
                    </span>
                    <p className="event-description">{action.description}</p>
                    <Link to={`/action/${action.id}`} className="event-link">Voir d√©tails</Link>
                  </div>
                </div>
              ))
            ) : (
              <p>Aucune action planifi√©e pour {months[selectedMonth]} {selectedYear}.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Planning;







