import React, { useState, useEffect } from 'react';
import { fetchActions, createAction, updateAction, deleteAction } from './services/api';
import ActionCard from './components/ActionCard';
import ActionForm from './components/ActionForm';
import SearchBar from './components/SearchBar';
import './App.css';

import { AuthProvider, useAuth } from './context/AuthContext';



function RSEPlatform() {
  //const { user } = useAuth();
  const { user, login, logout } = useAuth(); // âœ… ne sera plus undefined
  return (
    <div>
      {user ? <p>Bienvenue {user.email}</p> : <p>Veuillez vous connecter</p>}
    </div>
  );
  const [actions, setActions] = useState([]);
  const [filteredActions, setFilteredActions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [formData, setFormData] = useState({
    titre: "",
    description: "",
    date_debut: "",
    date_fin: "",
    statut: "planifiÃ©",
    createur_id: 1,
    populations: [],
    ressources: [],
    kpis: [],
  });
  const [populations, setPopulations] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  // Charger les donnÃ©es
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);

        const response = await fetchActions();
        const actionsData = response.data || []; // âœ… on prend bien response.data
        setActions(actionsData);
        setFilteredActions(actionsData);

        setPopulations([
          { id: 1, type: "Ã©tudiants" },
          { id: 2, type: "enseignants" },
          { id: 3, type: "admin" },
          { id: 4, type: "partenaires" },
          { id: 5, type: "tout" },
        ]);
      } catch (err) {
        setError("Erreur de chargement des donnÃ©es");
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  // Filtrer en fonction de la recherche
  useEffect(() => {
    const filtered = actions.filter(
      (action) =>
        action.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        action.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredActions(filtered);
  }, [searchTerm, actions]);

  // GÃ©rer les changements du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Soumettre (crÃ©ation ou mise Ã  jour)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await updateAction(editingId, formData);
        setActions(
          actions.map((a) =>
            a.id === editingId ? { ...a, ...formData } : a
          )
        );
      } else {
        const response = await createAction(formData);
        const newAction = response.data || response; // selon ton API
        setActions([...actions, newAction]);
      }
      resetForm();
    } catch (err) {
      setError(editingId ? "Erreur de mise Ã  jour" : "Erreur de crÃ©ation");
      console.error(err);
    }
  };

  const handleEdit = (action) => {
    setFormData({
      titre: action.titre,
      description: action.description,
      date_debut: action.date_debut,
      date_fin: action.date_fin,
      statut: action.statut,
      createur_id: action.createur_id,
      populations: action.populations || [],
      ressources: action.ressources || [],
      kpis: action.kpis || [],
    });
    setEditingId(action.id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Supprimer cette action ?")) return;
    try {
      await deleteAction(id);
      setActions(actions.filter((a) => a.id !== id));
    } catch (err) {
      setError("Erreur de suppression");
      console.error(err);
      setTimeout(() => setError(""), 3000);
    }
  };

  const resetForm = () => {
    setFormData({
      titre: "",
      description: "",
      date_debut: "",
      date_fin: "",
      statut: "planifiÃ©",
      createur_id: 1,
      populations: [],
      ressources: [],
      kpis: [],
    });
    setEditingId(null);
  };

  return (
    <div className="app">
      {/* HEADER */}
      <header className="header">
        <div className="header-content">
          <div className="logo-container">
            <img src="/ECE.png" alt="ECE Paris" className="logo" />
          </div>
          <h1 className="header-title">Plateforme RSE - ECE Paris</h1>

          <div className="user-avatar-container">
            {user ? (
              <div className="user-info">
                <span className="user-name">
                  {user.prenom || user.email.split("@")[0]}
                </span>
              </div>
            ) : (
              <div className="user-avatar">
                <span>U</span>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* MAIN */}
      <main className="main-container">
        <ActionForm
          formData={formData}
          onChange={handleChange}
          onSubmit={handleSubmit}
          onCancel={resetForm}
          isEditing={!!editingId}
          populations={populations}
        />

        <section className="actions-section">
          <div className="section-header">
            <h2>Actions RSE ({filteredActions.length})</h2>
            <div className="section-actions">
              <button
                className="refresh-btn"
                onClick={() => window.location.reload()}
              >
                ðŸ”„ Actualiser
              </button>
            </div>
          </div>

          <div className="action-search-container">
            <SearchBar value={searchTerm} onChange={setSearchTerm} />
          </div>

          {error && <div className="error-banner">{error}</div>}

          {isLoading ? (
            <div className="loading">Chargement en cours...</div>
          ) : !Array.isArray(filteredActions) ||
            filteredActions.length === 0 ? (
            <div className="empty-state">
              Aucune action trouvÃ©e. Essayez une autre recherche.
            </div>
          ) : (
            <div className="actions-grid">
              {filteredActions.map((action) => (
                <ActionCard
                  key={action.id}
                  action={action}
                  onEdit={handleEdit}
                  onDelete={handleDelete}
                />
              ))}
            </div>
          )}
        </section>
      </main>

      {/* FOOTER */}
      <footer className="footer">
        <p>Â© {new Date().getFullYear()} ECE Paris - Projet RSE</p>
      </footer>
    </div>
  );
}

//export default RSEPlatform;
export default RSEPlatform;
