import React, { useState, useEffect } from 'react';
import { fetchActions, createAction, updateAction, deleteAction } from './services/api';
import ActionCard from './components/ActionCard';
import ActionForm from './components/ActionForm';
import SearchBar from './components/SearchBar';

import './App.css';

import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import RSEPlatform from './RSEPlatform'; 
import Navbar from "./components/Navbar";
import Dashboard from './components/Dashboard';
import ActionDetail from './components/ActionDetail';
import Planning from './components/Planning';
import Newsletter from './components/Newsletter';
import Statistics from './components/Statistics';

/*function PrivateRoute({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/login" />;
}

/**************/

function PrivateRoute({ children, roles }) {
  const { user } = useContext(AuthContext);

  if (!user) return <Navigate to="/login" />;
  if (roles && !roles.includes(user.role)) return <Navigate to="/dashboard" />;

  return children;
}
 
/***************** */ 


function App() {
  return (
     <AuthProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={<Navigate to="/login" />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* ✅ Protégé */}
          <Route
            path="/RSEPlatform"
            element={
              <PrivateRoute>
                <RSEPlatform />
              </PrivateRoute>
            }
          />

            {/* Accessible à tous connectés */}
          <Route path="/dashboard" element={
            <PrivateRoute roles={["admin","etudiant","enseignant"]}>
              <Dashboard />
            </PrivateRoute>
          }/>
          
          <Route path="/actions/:id" element={
            <PrivateRoute roles={["admin","etudiant","enseignant"]}>
              <ActionDetail />
            </PrivateRoute>
          }/>

          {/* Accessible seulement admin */}
          <Route path="/create-action" element={
            <PrivateRoute roles={["admin"]}>
              <CreateAction />
            </PrivateRoute>
          }/>

          <Route path="*" element={<Navigate to="/login" />} />

         {/** <Route path="/dashboard" element={<Dashboard />} /> */}
           
          <Route path="/statistics" element={<Statistics />} />
          <Route path="/newsletter" element={<Newsletter />} />
          <Route path="/planning" element={<Planning />} />
           <Route path="/actions/:id" element={<ActionDetail />} />
          
          
        </Routes>
      </Router>
    </AuthProvider>
  );
}

      

