  const [filteredActions, setFilteredActions] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [formData, setFormData] = useState({
    titre: "",
    description: "",
    date_debut: "",
    date_fin: "",
    statut: "planifié",
    createur_id: 1,
    populations: [],
    ressources: [],
    kpis: [],
  });
  const [populations, setPopulations] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");

  // Charger les données
  useEffect(() => {
    const loadData = async () => {
      try {
        setIsLoading(true);

        const response = await fetchActions();
        const actionsData = response.data || []; // ✅ on prend bien response.data
        setActions(actionsData);
        setFilteredActions(actionsData);

        setPopulations([
          { id: 1, type: "étudiants" },
          { id: 2, type: "enseignants" },
          { id: 3, type: "admin" },
          { id: 4, type: "partenaires" },
          { id: 5, type: "tout" },
        ]);
      } catch (err) {
        setError("Erreur de chargement des données");
        console.error(err);
      } finally {
        setIsLoading(false);
      }
    };

    loadData();
  }, []);

  // Filtrer en fonction de la recherche
  useEffect(() => {
    const filtered = actions.filter(
      (action) =>
        action.titre.toLowerCase().includes(searchTerm.toLowerCase()) ||
        action.description.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredActions(filtered);
  }, [searchTerm, actions]);

  // Gérer les changements du formulaire
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Soumettre (création ou mise à jour)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await updateAction(editingId, formData);
        setActions(
          actions.map((a) =>
            a.id === editingId ? { ...a, ...formData } : a
          )
        );
      } else {
        const response = await createAction(formData);
        const newAction = response.data || response; // selon ton API
        setActions([...actions, newAction]);
      }
      resetForm();
    } catch (err) {
      setError(editingId ? "Erreur de mise à jour" : "Erreur de création");
      console.error(err);
    }
  };

