import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { loginUser } from "../../api"; // <-- on utilise l'API centralisée
import './Auth.css';

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  /*const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await login(formData.email, formData.password);
      navigate('/');
    } catch (err) {
      setError('Identifiants incorrects');
    }
  };*/

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/auth/login', credentials);
      
      // Stockez le token dans localStorage
      localStorage.setItem('authToken', response.data.token);
      
      // Redirigez vers la page d'accueil
      navigate('/');
    } catch (error) {
      // Gestion des erreurs
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2>Connexion à GreenCampus</h2>
        {error && <p className="error">{error}</p>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="form-group">
            <label>Mot de passe</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          <button type="submit" className="auth-btn">Se connecter</button>
        </form>
        
        <p className="auth-footer">
          Pas encore inscrit? <span onClick={() => navigate('/register')}>Créer un compte</span>
        </p>
      </div>
    </div>
  );
};

export default Login;
