import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:5000/api",
});

// Intercepteur pour ajouter le token automatiquement
API.interceptors.request.use((config) => {
  const token = localStorage.getItem("token"); // Standardisé sur "token"
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth
export const loginUser = (credentials) => API.post("/auth/login", credentials);
export const registerUser = (data) => API.post("/auth/register", data);

// Actions RSE
export const fetchActions = () => API.get("/action"); // Correction du endpoint (ajout du "s")
export const createAction = (action) => API.post("/action", action);
export const updateAction = (id, action) => API.put(`/action/${id}`, action);
export const deleteAction = (id) => API.delete(`/action/${id}`);










