import React, { useEffect, useState } from "react";
//import { fetchActions } from "./services/api";

import { useNavigate } from 'react-router-dom';
 import { loginUser } from '../services/api'; // Chemin corrigé

function App() {
  const [actions, setActions] = useState([]);
  const [isAuth, setIsAuth] = useState(!!localStorage.getItem("authToken"));

  useEffect(() => {
    const loadData = async () => {
      try {
        const res = await fetchActions();
        setActions(res.data);
      } catch (err) {
        console.error("Erreur chargement actions :", err.response?.data || err.message);
      }
    };

    if (isAuth) loadData();
  }, [isAuth]);

  return (
    <div>
      {isAuth ? (
        <div>
          <h1>Liste des actions</h1>
          <ul>
            {actions.map((a) => (
              <li key={a.id}>{a.title}</li>
            ))}
          </ul>
        </div>
      ) : (
        <Login />
      )}
    </div>
  );
}

export default App;








