import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const location = useLocation();
  const [isOpen, setIsOpen] = useState(false);

  // Simule un utilisateur connect√©
  const user = {
    name: "Jean Dupont",
    avatar: "https://ui-avatars.com/api/?name=Jean+Dupont&background=2563eb&color=fff"
  };

  // Cacher la navbar pour login et register
  if (location.pathname === "/login" || location.pathname === "/register") {
    return null;
  }

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/dashboard" className="app-title">
          üåç RSE App
        </Link>
      </div>

      {/* Bouton menu burger pour mobile */}
      <button
        className={`burger ${isOpen ? "open" : ""}`}
        onClick={() => setIsOpen(!isOpen)}
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      {/* Liens de navigation */}
      <ul className={`nav-links ${isOpen ? "active" : ""}`}>
        <li><Link to="/dashboard" onClick={() => setIsOpen(false)}>Dashboard</Link></li>
        <li><Link to="/planning" onClick={() => setIsOpen(false)}>Planning</Link></li>
        <li><Link to="/newsletter" onClick={() => setIsOpen(false)}>Newsletter</Link></li>
        <li><Link to="/create-action" onClick={() => setIsOpen(false)}>Cr√©er Action</Link></li>
      </ul>

      <div className="navbar-right">
        <img src={user.avatar} alt="avatar" className="user-avatar" />
        <span className="user-name">{user.name}</span>
      </div>
    </nav>
  );
};

export default Navbar;
