import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalActions: 0,
    completedActions: 0,
    upcomingActions: 0,
    participationRate: 0
  });

  const [recentActions, setRecentActions] = useState([]);

  useEffect(() => {
    // Simulation de données - à remplacer par un appel API
    setStats({
      totalActions: 24,
      completedActions: 12,
      upcomingActions: 5,
      participationRate: 76
    });

    setRecentActions([
      { id: 1, title: 'Journée de nettoyage', date: '2024-09-15', status: 'planned' },
      { id: 2, title: 'Atelier recyclage', date: '2024-10-02', status: 'in-progress' },
      { id: 3, title: 'Collecte de vêtements', date: '2024-08-20', status: 'completed' }
    ]);
  }, []);

  return (
    <div className="dashboard">
      <h1>Tableau de Bord RSE</h1>
      
      <div className="stats-grid">
        <div className="stat-card">
          <h3>Actions totales</h3>
          <p className="stat-number">{stats.totalActions}</p>
        </div>
        <div className="stat-card">
          <h3>Actions complétées</h3>
          <p className="stat-number">{stats.completedActions}</p>
        </div>
        <div className="stat-card">
          <h3>Actions à venir</h3>
          <p className="stat-number">{stats.upcomingActions}</p>
        </div>
        <div className="stat-card">
          <h3>Taux de participation</h3>
          <p className="stat-number">{stats.participationRate}%</p>
        </div>
      </div>

      <div className="dashboard-sections">
        <section className="recent-actions">
          <h2>Actions récentes</h2>
          <ul>
            {recentActions.map(action => (
              <li key={action.id} className="action-item">
                <span className={`status-indicator ${action.status}`}></span>
                <div className="action-info">
                  <h4>{action.title}</h4>
                  <p>Date: {action.date}</p>
                </div>
                <Link to={`/action/${action.id}`} className="view-details">Voir détails</Link>
              </li>
            ))}
          </ul>
        </section>

        <section className="quick-actions">
          <h2>Actions rapides</h2>
          <div className="action-buttons">
            <Link to="/create-action" className="btn btn-primary">Créer une action</Link>
            <Link to="/planning" className="btn btn-secondary">Voir le planning</Link>
            <Link to="/newsletter" className="btn btn-tertiary">Gérer newsletter</Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default Dashboard;
