// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import { AuthProvider, useAuth } from "./context/AuthContext";
import Navbar from "./components/Navbar";

import Login from "./components/Auth/Login";
import Register from "./components/Auth/Register";
import Dashboard from "./components/Dashboard";
import ActionDetail from "./components/ActionDetail";
import RSEPlatform from "./RSEPlatform";
import Planning from "./components/Planning";
import Newsletter from "./components/Newsletter";
import Statistics from "./components/Statistics";

import "./App.css";

function PrivateRoute({ children, roles }) {
  const { user, loading } = useAuth();
  
  if (loading) return <div>Chargement...</div>;
  if (!user) return <Navigate to="/login" replace />;
  
  const normalizedUserRole = user.role.toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, "");
  const allowedRoles = roles.map(role => role.toLowerCase().normalize("NFD").replace(/[\u0300-\u036f]/g, ""));
  
  if (!allowedRoles.includes(normalizedUserRole)) {
    return <Navigate to="/dashboard" replace />;
  }
   return children;
}

/* Route protégée
function PrivateRoute({ children, roles }) {
  const { user, loading, normalizeRole } = useAuth();

  if (loading) return null; // ou un spinner si tu veux
  if (!user) return <Navigate to="/login" replace />;

  if (roles && roles.length) {
    const userRole = normalizeRole(user.role);
    const allowed = roles.map(normalizeRole).includes(userRole);
    if (!allowed) return <Navigate to="/dashboard" replace />;
  }

  return children;
}*/

function App() {
  return (
    <AuthProvider>
      <Router>
        <Navbar />

        <Routes>
          <Route path="/" element={<Navigate to="/login" replace />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Étudiant/Enseignant/Admin */}
          <Route
            path="/dashboard"
            element={
              <PrivateRoute roles={["admin", "etudiant", "étudiant", "enseignant"]}>
                <Dashboard />
              </PrivateRoute>
            }
          />
          <Route
            path="/actions/:id"
            element={
              <PrivateRoute roles={["admin", "etudiant", "étudiant", "enseignant"]}>
                <ActionDetail />
              </PrivateRoute>
            }
          />

          {/* Admin uniquement */}
          <Route
            path="/RSEPlatform"
            element={
              <PrivateRoute roles={["admin"]}>
                <RSEPlatform />
              </PrivateRoute>
            }
          />
          <Route
            path="/planning"
            element={
              <PrivateRoute roles={["admin"]}>
                <Planning />
              </PrivateRoute>
            }
          />
          <Route
            path="/newsletter"
            element={
              <PrivateRoute roles={["admin"]}>
                <Newsletter />
              </PrivateRoute>
            }
          />
          <Route
            path="/statistics"
            element={
              <PrivateRoute roles={["admin"]}>
                <Statistics />
              </PrivateRoute>
            }
          />

          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;

















